# พิมพ์คำว่า 'สวัสดี' 3 ครั้ง
for _ in range(3):
    print('สวัสดี')

# ตรวจเลขคู่/คี่
n = int(input())
if n % 2 == 0:
    print('Even')
else:
    print('Odd')

# FizzBuzz
n = int(input())
for i in range(1, n+1):
    if i % 15 == 0:
        print('FizzBuzz')
    elif i % 3 == 0:
        print('Fizz')
    elif i % 5 == 0:
        print('Buzz')
    else:
        print(i)
